sub-flow.expression-component:
flowVars.wherePaged="order by EMPLOYEEID"+ ((message.inboundProperties['http.query.params'].offset==empty)? "": " OFFSET "+ message.inboundProperties['http.query.params'].offset  +" ROWS ") +
((message.inboundProperties['http.query.params'].limit==empty)? "": " FETCH NEXT "+ message.inboundProperties['http.query.params'].limit)  +
 " ROWS ONLY " ;

dw:transform-message.dw:set-payload:
%dw 1.0
%output application/json
%type currency = :number { format: "##.##"}
%var sortedBy = flowVars.sortedBy

//%var deptName = (inboundProperties['http.query.params'].deptName==empty)? "": inboundProperties['http.query.params'].deptName
---

payload map ((payload01 , indexOfPayload01) -> {
	employeeID: payload01.EMPLOYEEID,
	employeeName: payload01.EMPLOYEENAME,
	deptName:payload01.DEPTNAME,
	salary:payload01.SALARY as :currency
}) filter $.salary > 5000 and $.deptName=="HR" orderBy sortedBy
